import React, { FC, MutableRefObject, useRef } from "react";

const AddReview: FC = () => {
  const titleRef = useRef(null);
  const ratingRef = useRef(null);
  const review = useRef(null);

  // TODO : create label and input for rating and review
  // TODO : create handleSubmit in parent component and pass to AddReview component
  return (
    <div>
      <form>
        <label htmlFor="title">
          Title: 
          <input type="text" name="title" ref={titleRef} id="title" />
        </label>
        <label htmlFor="rating">
          Rating: 
          <input type="range" name="rating" ref={ratingRef} id="rating" min="0" max="5" step="0.5"/>
        </label>
      </form>
    </div>
  );
};

export default AddReview;
