import React, { useState, useEffect, ChangeEvent, FormEvent } from "react";
import "../styles/test.css";

// Define Review type
interface Review {
  name: string;
  text: string;
}

// Review Component
const Review: React.FC<{ review: Review }> = ({ review }) => (
  <div className="review">
    <p>
      <strong>{review.name}</strong>
    </p>
    <p>{review.text}</p>
  </div>
);

// Main Component
const ReviewApp: React.FC = () => {
  const [reviews, setReviews] = useState<Review[]>([]);
  const [name, setName] = useState<string>("");
  const [text, setText] = useState<string>("");

  // Sample effect: fetch existing reviews (simulated)
  useEffect(() => {
    // Mock initial reviews data (could be replaced with API call)
    const initialReviews: Review[] = [
      { name: "John", text: "Great product!" },
      { name: "Jane", text: "Loved it, highly recommend." },
    ];
    setReviews(initialReviews);
  }, []);

  // Handle form submission
  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();
    if (name && text) {
      const newReview: Review = { name, text };
      setReviews([...reviews, newReview]);
      setName("");
      setText("");
    }
  };

  // Handle input changes
  const handleNameChange = (e: ChangeEvent<HTMLInputElement>) => {
    setName(e.target.value);
  };

  const handleTextChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
    setText(e.target.value);
  };

  return (
    <div className="review-app">
      <h1>Product Reviews</h1>

      {/* Review Form */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input
            type="text"
            value={name}
            onChange={handleNameChange}
            required
          />
        </div>
        <div>
          <label>Review</label>
          <textarea value={text} onChange={handleTextChange} required />
        </div>
        <button type="submit">Submit Review</button>
      </form>

      {/* Display Reviews */}
      <div className="reviews">
        <h2>All Reviews</h2>
        {reviews.length > 0 ? (
          reviews.map((review, index) => <Review key={index} review={review} />)
        ) : (
          <p>No reviews yet</p>
        )}
      </div>
    </div>
  );
};

export default ReviewApp;
