// Reviews.tsx
import React from "react";

// ReviewProps Interface
interface ReviewProps {
  name: string;
  date: string;
  rating: number;
  title: string;
  content: string;
}

// StarRating component
const StarRating: React.FC<{ rating: number }> = ({ rating }) => {
  const totalStars = 5;
  return (
    <div>
      {[...Array(totalStars)].map((_, index) => (
        <span key={index}>{index < rating ? "⭐" : "☆"}</span>
      ))}
    </div>
  );
};

// Review Component
const Review: React.FC<ReviewProps> = ({
  name,
  date,
  rating,
  title,
  content,
}) => {
  return (
    <div style={styles.reviewContainer}>
      <div style={styles.header}>
        <span>{name}</span>
        <span>{date}</span>
      </div>
      <div style={styles.rating}>
        <StarRating rating={rating} />
        <span>{rating}</span>
      </div>
      <h3 style={styles.title}>{title}</h3>
      <p>{content}</p>
    </div>
  );
};

// Main Reviews component
const Reviews: React.FC = () => {
  const reviewsData: ReviewProps[] = [
    {
      name: "Risako M",
      date: "May 16, 2021",
      rating: 5,
      title: "Can't say enough good things",
      content: `I was really pleased with the overall shopping experience. My order even included a little personal, handwritten note, which delighted me!
            The product quality is amazing, it looks and feels even better than I had anticipated. Brilliant stuff! I would gladly recommend this store to my friends. And, now that I think of it... I actually have, many times!`,
    },
    {
      name: "Jackie H",
      date: "April 6, 2021",
      rating: 5,
      title: "Very comfy and looks the part",
      content: `After a quick chat with customer support, I had a good feeling about this shirt and ordered three of them. 
            Less than 48 hours later, my delivery arrived. I haven't worn anything else since that day! These shirts are so comfortable, yet look classy enough that I can wear them at work or even some formal events. Winning!`,
    },
    {
      name: "Laura G",
      date: "February 24, 2021",
      rating: 4,
      title: "The last shirts I may ever need",
      content: `I bought two of those comfy cotton shirts, and let me tell you: they're amazing! I have been wearing them almost every day. 
            Even after a dozen washes, that still looks and feels good as new. Will definitely order a few more... If I ever need to!`,
    },
  ];

  return (
    <div>
      <h2>Recent reviews</h2>
      {reviewsData.map((review, index) => (
        <Review key={index} {...review} />
      ))}
    </div>
  );
};

// Basic inline styles
const styles = {
  reviewContainer: {
    borderBottom: "1px solid #eaeaea",
    padding: "20px 0",
    marginBottom: "20px",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    fontSize: "14px",
    color: "#777",
    marginBottom: "10px",
  },
  rating: {
    display: "flex",
    alignItems: "center",
  },
  title: {
    margin: "10px 0",
    fontSize: "16px",
    fontWeight: "bold",
  },
};

export default Reviews;
